{-# LANGUAGE TupleSections #-}
{-# OPTIONS_GHC -O2 #-}
import Control.Monad
import Data.List (inits,tails)
import Data.Map (fromListWith,Map,fold)
import qualified Data.Map as Map (filter)

main :: IO ()
main = getLine >>= putStrLn . show . palindromicBorderCount

substrings s = filter pred $ concatMap inits $ tails s
  where pred = join $ (. (s /=)) . (&&) . ("" /=)

isPalindrome = join $ (==) . reverse

palindromes = filter isPalindrome . substrings

freq :: String -> [String] -> Map String Integer
freq s l = fromListWith (+) $ map (,1) l

palindromicBorders = Map.filter (> 1) . (join $ (. palindromes) . freq)

isConst s = s == take (length s) (repeat $ head s)

pairCount = join $ (flip div 2 .) . (*) . pred

modularize = flip mod 1000000007

range s = [2..fromIntegral $ length s]

palindromicBorderCount :: String -> Integer
palindromicBorderCount s | isConst s = modularize $ sum [pairCount i | i <- range s]
palindromicBorderCount s = fold ((modularize .) . (+) . pairCount) 0 . palindromicBorders $ s

input = ""